// src/pages/ProfilePage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate }      from 'react-router-dom';
import api                   from '../api';
import Post                  from '../components/Post';

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem('user') || 'null');
  const userId     = storedUser?.userId;
  const navigate   = useNavigate();

  const [user, setUser]             = useState(null);
  const [posts, setPosts]           = useState([]);
  const [loading, setLoading]       = useState(true);
  const [tab, setTab]               = useState('posts'); // 'posts' | 'likes' | 'favorites'

  // Загрузить профиль один раз
  useEffect(() => {
    if (!userId) return navigate('/login');
    api.get(`/users/${userId}`)
      .then(({ data }) => setUser(data))
      .catch(console.error);
  }, [userId, navigate]);

  // При смене таба или пользователя — подгрузить нужные посты
  useEffect(() => {
    if (!userId) return;
    setLoading(true);

    // собираем query
    let params = { author: userId };
    if (tab === 'likes')      params = { likedBy: userId };
    else if (tab === 'favorites') params = { favoritedBy: userId };

    api.get('/posts', { params })
      .then(({ data }) => setPosts(data))
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [tab, userId]);

  if (!user) {
    return <div className="text-center mt-12 text-gray-500">Загрузка профиля…</div>;
  }

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow">
      {/* Header */}
      <div className="flex items-center space-x-6 mb-6">
        <div className="w-20 h-20 bg-green-500 rounded-full flex items-center justify-center text-white text-3xl font-bold">
          {user.username.slice(0,2).toUpperCase()}
        </div>
        <div>
          <h1 className="text-2xl font-bold">{user.fullname}</h1>
          <p className="text-gray-500">@{user.username}</p>
        </div>
      </div>

      {/* Tabs */}
      <div className="flex space-x-2 mb-6">
        {['posts','likes','favorites'].map(t => (
          <button
            key={t}
            onClick={() => setTab(t)}
            className={`flex-1 text-center py-2 rounded-lg font-semibold
              ${tab===t 
                ? 'bg-green-600 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
          >
            {t === 'posts'      ? 'Посты' 
             : t === 'likes'     ? 'Лайки' 
             : /* favorites */     'Избранное'}
          </button>
        ))}
      </div>

      {/* Content */}
      {loading ? (
        <div className="text-center text-gray-500">Загрузка…</div>
      ) : posts.length === 0 ? (
        <div className="text-gray-500">
          {tab === 'posts'      ? 'У вас пока нет постов.'
           : tab === 'likes'     ? 'Вы ещё не ставили лайки.'
           : /* favorites */      'У вас нет избранного.'}
        </div>
      ) : (
        posts.map(post => <Post key={post._id} post={post} />)
      )}
    </div>
  );
}
