// src/pages/ProfilePage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate }             from 'react-router-dom';
import { MoreVertical }            from 'lucide-react';
import api                         from '../api';
import Post                        from '../components/Post';

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem('user') || 'null');
  const userId     = storedUser?.userId;
  const navigate   = useNavigate();

  const [user,          setUser]          = useState(null);
  const [posts,         setPosts]         = useState([]);
  const [likedPosts,    setLikedPosts]    = useState([]);
  const [favoritePosts, setFavoritePosts] = useState([]);
  const [loading,       setLoading]       = useState(true);

  const [tab,       setTab]       = useState('posts');
  const [isEditing, setIsEditing] = useState(false);
  const [fullname,  setFullname]  = useState('');
  const [username,  setUsername]  = useState('');
  const [bio,       setBio]       = useState('');
  const [location,  setLocation]  = useState('');
  const [saving,    setSaving]    = useState(false);

  // Для удаления
  const [deletePostId, setDeletePostId] = useState(null);
  const [deleting,     setDeleting]     = useState(false);

  // 1) Загрузить профиль
  useEffect(() => {
    if (!userId) return navigate('/login');
    api.get(`/users/${userId}`)
      .then(({ data }) => {
        setUser(data);
        setFullname(data.fullname);
        setUsername(data.username);
        setBio(data.bio || '');
        setLocation(data.location || '');
      })
      .catch(console.error);
  }, [userId, navigate]);

  // 2) Загрузка постов по табу
  useEffect(() => {
    if (!userId) return;
    setLoading(true);

    let params = {};
    if (tab === 'posts') params = { author: userId };
    if (tab === 'likes') params = { likedBy: userId };
    if (tab === 'favorites') params = { favoritedBy: userId };

    api.get('/posts', { params })
      .then(({ data }) => {
        if (tab === 'posts') setPosts(data);
        if (tab === 'likes') setLikedPosts(data);
        if (tab === 'favorites') setFavoritePosts(data);
      })
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [tab, userId]);

  // 3) Сохранение профиля
  const handleSave = async () => {
    setSaving(true);
    try {
      const { data } = await api.put(`/users/${userId}`, {
        fullname, username, bio, location
      });
      setUser(data);
      localStorage.setItem('user', JSON.stringify({
        ...storedUser,
        fullname: data.fullname,
        username: data.username
      }));
      setIsEditing(false);
    } catch (err) {
      console.error(err);
    }
    setSaving(false);
  };

  // 4) Удаление поста
  const confirmDelete = async () => {
    setDeleting(true);
    try {
      await api.delete(`/posts/${deletePostId}`);
      // после удаления — перезагрузим текущий таб
      setDeletePostId(null);
      setTab('posts');
      // рефетчим
      const { data } = await api.get('/posts', { params: { author: userId } });
      setPosts(data);
    } catch (err) {
      console.error(err);
    }
    setDeleting(false);
  };

  if (!user) {
    return <div className="text-center mt-12 text-gray-500">Загрузка профиля…</div>;
  }

  const initials = user.username.slice(0,2).toUpperCase();

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow">

      {/* ----- Profile Header ----- */}
      <div className="flex items-center space-x-6 mb-6">
        <div className="w-20 h-20 bg-green-500 rounded-full flex items-center justify-center text-white text-3xl font-bold">
          {initials}
        </div>
        <div>
          <h1 className="text-2xl font-bold">{user.fullname}</h1>
          <p className="text-gray-500">@{user.username}</p>
        </div>
        <button
          onClick={() => setIsEditing(prev => !prev)}
          className="ml-auto px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-100"
        >
          {isEditing ? 'Отменить' : 'Редактировать профиль'}
        </button>
      </div>

      {/* ----- Edit Form / Display ----- */}
      {isEditing ? (
        <div className="space-y-4 mb-6">
          {/* fullname, username, bio, location inputs (как было) */}
          {/* ... */}
          <button
            onClick={handleSave}
            disabled={saving}
            className="px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50"
          >
            {saving ? 'Сохраняем...' : 'Сохранить'}
          </button>
        </div>
      ) : (
        <div className="mb-6 space-y-2">
          {user.bio && <p className="text-gray-700">{user.bio}</p>}
          {user.location && (
            <p className="flex items-center text-gray-600">
              <svg className="w-5 h-5 mr-2 text-gray-500" fill="currentColor" viewBox="0 0 20 20">
                <path d="M10 2C6.686 2 4 4.686 4 8c0 4.5 6 10 6 10s6-5.5 6-10c0-3.314-2.686-6-6-6zm0 8a2 2 0 110-4 2 2 0 010 4z"/>
              </svg>
              {user.location}
            </p>
          )}
        </div>
      )}

      {/* ----- Tabs ----- */}
      <div className="flex space-x-2 mb-6">
        {['posts','likes','favorites'].map(t => (
          <button
            key={t}
            onClick={() => setTab(t)}
            className={`flex-1 py-2 text-center rounded-lg font-semibold ${
              tab===t ? 'bg-green-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            {t==='posts'?'Посты':t==='likes'?'Лайки':'Избранное'}
          </button>
        ))}
      </div>

      {/* ----- Posts List WITH DELETE MENU ----- */}
      {loading ? (
        <div className="text-center text-gray-500">Загрузка…</div>
      ) : (
        (tab==='posts' ? posts : tab==='likes' ? likedPosts : favoritePosts).map(p => (
          <div key={p._id} className="relative group">
            {/* Three-dots menu */}
            {tab==='posts' && (
              <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100">
                <button
                  onClick={() => setDeletePostId(p._id)}
                  className="p-1 hover:bg-gray-100 rounded-full"
                  aria-label="Меню поста"
                >
                  <MoreVertical className="w-5 h-5 text-gray-500" />
                </button>
              </div>
            )}
            <Post post={p} />
          </div>
        ))
      )}

      {/* ----- Delete Confirmation Modal ----- */}
      {deletePostId && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-sm">
            <h3 className="text-lg font-semibold mb-4">Удалить пост?</h3>
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => setDeletePostId(null)}
                className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-100"
              >
                Нет
              </button>
              <button
                onClick={confirmDelete}
                disabled={deleting}
                className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 disabled:opacity-50"
              >
                {deleting ? 'Удаляем...' : 'Да, удалить'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
