import { useEffect, useState } from 'react';
import api from '../api';
import Post from '../components/Post';

export default function ProfilePage() {
  const [text, setText] = useState('');
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);

  // üî¥ –ó–∞–º–µ–Ω–∏—Ç–µ –Ω–∞ —Å–≤–æ–µ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ MongoDB
  const userId = '681865a282024c53751b2821';

  const fetchOwnPosts = () => {
    api.get('/posts')
      .then(({ data }) => {
        setPosts(data.filter(p => p.author._id === userId));
      })
      .catch(console.error);
  };

  useEffect(() => {
    fetchOwnPosts();
  }, []);

  const handleSubmit = async e => {
    e.preventDefault();
    if (!text.trim()) return;
    setLoading(true);
    try {
      await api.post('/posts', { author: userId, text });
      setText('');
      fetchOwnPosts();
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  return (
    <div className="max-w-2xl mx-auto">
      <h2 className="text-3xl font-bold mb-6">–ü—Ä–æ—Ñ–∏–ª—å</h2>
      <form onSubmit={handleSubmit} className="mb-8">
        <textarea
          className="w-full p-4 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-green-500"
          rows="4"
          placeholder="–ß—Ç–æ —É –≤–∞—Å –Ω–æ–≤–æ–≥–æ?"
          value={text}
          onChange={e => setText(e.target.value)}
        />
        <button
          type="submit"
          disabled={loading}
          className="px-6 py-3 bg-green-600 text-white font-semibold rounded-lg hover:bg-green-700 disabled:opacity-50"
        >
          {loading ? '–ü—É–±–ª–∏–∫—É–µ–º...' : '–û–ø—É–±–ª–∏–∫–æ–≤–∞—Ç—å'}
        </button>
      </form>
      <h3 className="text-2xl font-semibold mb-4">–ú–æ–∏ –ø–æ—Å—Ç—ã</h3>
      {posts.length === 0 ? (
        <div className="text-gray-500">–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç –ø–æ—Å—Ç–æ–≤.</div>
      ) : (
        posts.map(post => <Post key={post._id} post={post} />)
      )}
    </div>
  );
}
