// src/pages/UserPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api';
import Post from '../components/Post';

export default function UserPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [user,    setUser]    = useState(null);
  const [posts,   setPosts]   = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!id) return navigate('/feed');

    (async () => {
      try {
        // 1. Профиль
        const { data: userData } = await api.get(`/users/${id}`);
        setUser(userData);

        // 2. Посты этого пользователя
        const { data: allPosts } = await api.get('/posts');
        const userPosts = allPosts.filter(p => p.author._id === id);
        setPosts(userPosts);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    })();
  }, [id, navigate]);

  if (loading) {
    return <div className="text-center mt-12 text-gray-500">Загрузка...</div>;
  }
  if (!user) {
    return <div className="text-center mt-12 text-red-500">Пользователь не найден</div>;
  }

  // Инициалируем аватар двумя первыми буквами username
  const initials = user.username.slice(0, 2).toUpperCase();

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow">
      {/* Шапка пользователя */}
      <div className="flex items-center mb-6">
        <div className="w-16 h-16 bg-green-500 rounded-full flex items-center justify-center text-white text-2xl font-bold mr-4">
          {initials}
        </div>
        <div>
          <h1 className="text-2xl font-bold">{user.fullname}</h1>
          <p className="text-gray-500">@{user.username}</p>
        </div>
      </div>

      {/* Описание */}
      {user.bio && <p className="mb-4 text-gray-700">{user.bio}</p>}
      {user.location && (
        <p className="mb-6 flex items-center text-gray-600">
          <svg
            className="w-5 h-5 mr-2 text-gray-500"
            fill="currentColor"
            viewBox="0 0 20 20"
          >
            <path d="M10 2C6.686 2 4 4.686 4 8c0 4.5 6 10 6 10s6-5.5 6-10c0-3.314-2.686-6-6-6zm0 8a2 2 0 110-4 2 2 0 010 4z" />
          </svg>
          {user.location}
        </p>
      )}

      {/* Список постов */}
      <h2 className="text-xl font-semibold mb-4">Посты {user.username}</h2>
      {posts.length === 0 ? (
        <div className="text-gray-500">У пользователя ещё нет постов.</div>
      ) : (
        posts.map(post => <Post key={post._id} post={post} />)
      )}
    </div>
  );
}
