// src/pages/ProfilePage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate }             from 'react-router-dom';
import { MoreVertical, Edit2 }     from 'lucide-react';
import api                         from '../api';
import Post                        from '../components/Post';

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem('user') || 'null');
  const userId     = storedUser?.userId;
  const navigate   = useNavigate();

  const [user,          setUser]          = useState(null);
  const [posts,         setPosts]         = useState([]);
  const [likedPosts,    setLikedPosts]    = useState([]);
  const [favoritePosts, setFavoritePosts] = useState([]);
  const [loading,       setLoading]       = useState(true);

  const [tab,       setTab]       = useState('posts');    // 'posts' | 'likes' | 'favorites'
  const [isEditing, setIsEditing] = useState(false);
  const [fullname,  setFullname]  = useState('');
  const [username,  setUsername]  = useState('');
  const [bio,       setBio]       = useState('');
  const [location,  setLocation]  = useState('');
  const [saving,    setSaving]    = useState(false);

  // deletion
  const [deletePostId, setDeletePostId] = useState(null);
  const [deleting,     setDeleting]     = useState(false);

  // edit post
  const [activeMenuPostId, setActiveMenuPostId] = useState(null);
  const [editingPostId,    setEditingPostId]    = useState(null);
  const [editingText,      setEditingText]      = useState('');
  const [editingSaving,    setEditingSaving]    = useState(false);

  // 1) load profile
  useEffect(() => {
    if (!userId) return navigate('/login');
    api.get(`/users/${userId}`)
      .then(({ data }) => {
        setUser(data);
        setFullname(data.fullname);
        setUsername(data.username);
        setBio(data.bio || '');
        setLocation(data.location || '');
      })
      .catch(console.error);
  }, [userId, navigate]);

  // 2) load posts for current tab
  useEffect(() => {
    if (!userId) return;
    setLoading(true);
    let params = {};
    if (tab === 'posts')      params = { author: userId };
    if (tab === 'likes')      params = { likedBy: userId };
    if (tab === 'favorites')  params = { favoritedBy: userId };

    api.get('/posts', { params })
      .then(({ data }) => {
        if (tab === 'posts')      setPosts(data);
        else if (tab === 'likes') setLikedPosts(data);
        else                      setFavoritePosts(data);
      })
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [tab, userId]);

  // 3) save profile
  const handleSave = async () => {
    setSaving(true);
    try {
      const { data } = await api.put(`/users/${userId}`, {
        fullname, username, bio, location
      });
      setUser(data);
      localStorage.setItem('user', JSON.stringify({
        ...storedUser,
        fullname: data.fullname,
        username: data.username
      }));
      setIsEditing(false);
    } catch (err) {
      console.error(err);
    }
    setSaving(false);
  };

  // 4) confirm delete
  const confirmDelete = async () => {
    setDeleting(true);
    try {
      await api.delete(`/posts/${deletePostId}`);
      // reload posts
      setDeletePostId(null);
      const params = { author: userId };
      const { data } = await api.get('/posts', { params });
      setPosts(data);
    } catch (err) {
      console.error(err);
    }
    setDeleting(false);
  };

  // 5) start editing post
  const openEditModal = post => {
    setEditingPostId(post._id);
    setEditingText(post.text);
    setActiveMenuPostId(null);
  };

  // 6) confirm edit
  const confirmEdit = async () => {
    setEditingSaving(true);
    try {
      await api.put(`/posts/${editingPostId}`, { text: editingText });
      // reload posts
      const params = { author: userId };
      const { data } = await api.get('/posts', { params });
      setPosts(data);
      setEditingPostId(null);
    } catch (err) {
      console.error(err);
    }
    setEditingSaving(false);
  };

  if (!user) {
    return <div className="text-center mt-12 text-gray-500">Загрузка профиля…</div>;
  }

  const initials = user.username.slice(0,2).toUpperCase();
  const activePosts = tab === 'posts' ? posts : tab === 'likes' ? likedPosts : favoritePosts;

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow">
      {/* Profile header, edit form... (unchanged) */}

      {/* Tabs */}
      <div className="flex space-x-2 mb-6">
        {['posts','likes','favorites'].map(t => (
          <button
            key={t}
            onClick={() => setTab(t)}
            className={`flex-1 py-2 text-center rounded-lg font-semibold ${
              tab===t ? 'bg-green-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            {t==='posts'?'Посты':t==='likes'?'Лайки':'Избранное'}
          </button>
        ))}
      </div>

      {/* Posts list with menu */}
      {loading ? (
        <div className="text-center text-gray-500">Загрузка…</div>
      ) : activePosts.length === 0 ? (
        <div className="text-gray-500">
          {tab==='posts'
            ? 'У вас пока нет постов.'
            : tab==='likes'
            ? 'Вы ещё не ставили лайки.'
            : 'У вас нет избранного.'}
        </div>
      ) : (
        activePosts.map(p => (
          <div key={p._id} className="relative group">
            {tab === 'posts' && (
              <button
                onClick={() => setActiveMenuPostId(p._id)}
                className="absolute top-2 right-2 p-1 hover:bg-gray-100 rounded-full"
                aria-label="Меню поста"
              >
                <MoreVertical className="w-5 h-5 text-gray-500" />
              </button>
            )}
            {/* menu dropdown */}
            {activeMenuPostId === p._1 && false} {/* fix below */}
            {activeMenuPostId === p._id && (
              <div className="absolute top-8 right-2 bg-white border rounded-lg shadow-md z-10">
                <button
                  onClick={() => openEditModal(p)}
                  className="flex items-center px-4 py-2 space-x-2 hover:bg-gray-100 w-full text-left"
                >
                  <Edit2 className="w-4 h-4" /><span>Редактировать</span>
                </button>
                <button
                  onClick={() => { setDeletePostId(p._id); setActiveMenuPostId(null); }}
                  className="px-4 py-2 hover:bg-gray-100 w-full text-left text-red-600"
                >
                  Удалить
                </button>
              </div>
            )}
            <Post post={p} />
          </div>
        ))
      )}

      {/* Delete confirmation modal */}
      {deletePostId && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-20">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-sm">
            <h3 className="text-lg font-semibold mb-4">Удалить пост?</h3>
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => setDeletePostId(null)}
                className="px-4 py-2 border rounded-lg hover:bg-gray-100"
              >
                Нет
              </button>
              <button
                onClick={confirmDelete}
                disabled={deleting}
                className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 disabled:opacity-50"
              >
                {deleting ? 'Удаляем...' : 'Да, удалить'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Edit post modal */}
      {editingPostId && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-20">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Редактировать пост</h3>
            <textarea
              rows="4"
              value={editingText}
              onChange={e => setEditingText(e.target.value)}
              className="w-full p-3 border rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-green-500"
            />
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => setEditingPostId(null)}
                className="px-4 py-2 border rounded-lg hover:bg-gray-100"
              >
                Отменить
              </button>
              <button
                onClick={confirmEdit}
                disabled={editingSaving}
                className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50"
              >
                {editingSaving ? 'Сохраняем...' : 'Сохранить'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
