// src/components/Navbar.jsx
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';

export default function Navbar() {
  const { pathname } = useLocation();
  const navigate = useNavigate();

  // Смотрим, залогинен ли пользователь
  const [userId, setUserId] = useState(localStorage.getItem('userId'));

  // Обновляем состояние при изменении localStorage (на случай других вкладок)
  useEffect(() => {
    function onStorage() {
      setUserId(localStorage.getItem('userId'));
    }
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
  }, []);

  const handleLogout = () => {
    // Удаляем идентификатор, перенаправляем
    localStorage.removeItem('userId');
    setUserId(null);
    navigate('/login');
  };

  return (
    <nav className="bg-white shadow">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-green-600">Bailanysta</h1>
        <div className="space-x-6 flex items-center">
          <Link
            to="/feed"
            className={`hover:text-green-600 ${pathname === '/feed' && 'font-semibold text-green-600'}`}
          >
            Лента
          </Link>
          {userId && (
            <Link
              to="/profile"
              className={`hover:text-green-600 ${pathname === '/profile' && 'font-semibold text-green-600'}`}
            >
              Профиль
            </Link>
          )}

          {/* Показываем Регистрацию/Вход, когда нет userId */}
          {!userId && (
            <>
              <Link
                to="/register"
                className={`hover:text-green-600 ${pathname === '/register' && 'font-semibold text-green-600'}`}
              >
                Регистрация
              </Link>
              <Link
                to="/login"
                className={`hover:text-green-600 ${pathname === '/login' && 'font-semibold text-green-600'}`}
              >
                Вход
              </Link>
            </>
          )}

          {/* Показываем кнопку Выход, когда есть userId */}
          {userId && (
            <button
              onClick={handleLogout}
              className="text-red-500 hover:text-red-600 font-semibold"
            >
              Выход
            </button>
          )}
        </div>
      </div>
    </nav>
  );
}
