import { useEffect, useState } from 'react';
import api from '../api';
import Post from '../components/Post';

export default function ProfilePage() {
  // Читаем из localStorage
  const storedUser = JSON.parse(localStorage.getItem('user') || '{}');
  const userId = storedUser.userId;
  const username = storedUser.username;

  const [text, setText] = useState('');
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);

  // Функция загрузки только своих постов
  const fetchOwnPosts = () => {
    api.get('/posts')
      .then(({ data }) => {
        const my = data.filter(p => p.author._id === userId);
        setPosts(my);
      })
      .catch(console.error);
  };

  useEffect(() => {
    if (userId) {
      fetchOwnPosts();
    }
  }, [userId]);

  const handleSubmit = async e => {
    e.preventDefault();
    if (!text.trim()) return;
    setLoading(true);

    try {
      // отправляем author=userId, text
      await api.post('/posts', { author: userId, text });
      setText('');
      fetchOwnPosts();
    } catch (err) {
      console.error(err);
    }

    setLoading(false);
  };

  if (!userId) {
    return <div className="text-center mt-12">Пожалуйста, войдите в аккаунт.</div>;
  }

  return (
    <div className="max-w-2xl mx-auto">
      <h2 className="text-3xl font-bold mb-4">Привет, {username}!</h2>
      <form onSubmit={handleSubmit} className="mb-8">
        <textarea
          className="w-full p-4 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-green-500"
          rows="4"
          placeholder="Что у вас нового?"
          value={text}
          onChange={e => setText(e.target.value)}
        />
        <button
          type="submit"
          disabled={loading}
          className="px-6 py-3 bg-green-600 text-white font-semibold rounded-lg hover:bg-green-700 disabled:opacity-50"
        >
          {loading ? 'Публикуем...' : 'Опубликовать'}
        </button>
      </form>

      <h3 className="text-2xl font-semibold mb-4">Мои посты</h3>
      {posts.length === 0 ? (
        <div className="text-gray-500">У вас пока нет постов.</div>
      ) : (
        posts.map(post => <Post key={post._id} post={post} />)
      )}
    </div>
  );
}
