import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import api from '../api';
import { Heart, Star } from 'lucide-react';

export default function PostPage() {
  const { id } = useParams();
  const user    = JSON.parse(localStorage.getItem('user') || 'null');
  const userId  = user?.userId;

  const [post, setPost]         = useState(null);
  const [comments, setComments] = useState([]);
  const [loading, setLoading]   = useState(true);
  const [newComment, setNewComment] = useState('');
  const [commenting, setCommenting] = useState(false);

  const fetchData = async () => {
    setLoading(true);
    try {
      const { data } = await api.get(`/posts/${id}`);
      setPost(data);
      setComments(data.comments);
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, [id]);

  const handleLike = async () => {
    await api.post(`/posts/${id}/like`, { userId });
    fetchData();
  };

  const handleFavorite = async () => {
    await api.post(`/posts/${id}/favorite`, { userId });
    fetchData();
  };

  const handleComment = async () => {
    if (!newComment.trim()) return;
    setCommenting(true);
    await api.post(`/posts/${id}/comments`, {
      author: userId,
      text: newComment.trim()
    });
    setNewComment('');
    fetchData();
    setCommenting(false);
  };

  const handleCommentLike = async commentId => {
    await api.post(`/posts/comments/${commentId}/like`, { userId });
    fetchData();
  };

  if (loading) return <div className="text-center mt-8">Загрузка...</div>;
  if (!post)  return <div className="text-center mt-8 text-red-500">Пост не найден</div>;

  const liked   = post.likes.includes(userId);
  const favored = post.favorites.includes(userId);

  return (
    <div className="max-w-2xl mx-auto bg-white p-6 rounded-lg shadow">
      {/* Пост */}
      <div className="flex items-center mb-4">
        <div className="font-semibold">{post.author.username}</div>
        <div className="text-xs text-gray-500 ml-2">
          {new Date(post.createdAt).toLocaleString()}
        </div>
      </div>
      <p className="mb-4">{post.text}</p>
      <div className="flex items-center mb-6 space-x-4">
        <button onClick={handleLike} className="flex items-center">
          <Heart className={liked ? 'text-red-500' : ''} />
          <span className="ml-1">{post.likes.length}</span>
        </button>
        <button onClick={handleFavorite} className="flex items-center">
          <Star className={favored ? 'text-yellow-500' : ''} />
          <span className="ml-1">{post.favorites.length}</span>
        </button>
        <span className="ml-auto text-gray-500">
          {comments.length} коммент.
        </span>
      </div>

      {/* Форма комментария */}
      <div className="mb-6">
        <textarea
          rows="3"
          value={newComment}
          onChange={e => setNewComment(e.target.value)}
          className="w-full p-3 border rounded-lg mb-2 focus:ring-2 focus:ring-green-500"
          placeholder="Добавить комментарий..."
        />
        <button
          onClick={handleComment}
          disabled={commenting}
          className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50"
        >
          {commenting ? 'Отправка...' : 'Комментировать'}
        </button>
      </div>

      {/* Комментарии */}
      <h3 className="text-xl font-semibold mb-4">Комментарии</h3>
      {comments.length === 0 ? (
        <div className="text-gray-500">Нет комментариев.</div>
      ) : (
        comments.map(c => (
          <div key={c._id} className="p-4 bg-gray-50 rounded-lg mb-4">
            <div className="flex justify-between items-center mb-1">
              <div className="font-semibold">{c.author.username}</div>
              <div className="text-xs text-gray-500">{new Date(c.createdAt).toLocaleString()}</div>
            </div>
            <p className="mb-2">{c.text}</p>
            <button
              onClick={() => handleCommentLike(c._id)}
              className="flex items-center text-sm text-gray-600 hover:text-red-500"
            >
              ❤️ <span className="ml-1">{c.likes.length}</span>
            </button>
          </div>
        ))
      )}
    </div>
  );
}
