import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { MoreVertical, Edit2 } from 'lucide-react';
import api from '../api';
import Post from '../components/Post';

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem('user') || 'null');
  const userId = storedUser?.userId;
  const navigate = useNavigate();

  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [tab, setTab] = useState('posts');
  const [loading, setLoading] = useState(true);

  // Для меню: удалить/редактировать
  const [menuPostId, setMenuPostId] = useState(null);
  // Для удаления
  const [deletePostId, setDeletePostId] = useState(null);
  const [deleting, setDeleting] = useState(false);
  // Для редактирования
  const [editPostId, setEditPostId] = useState(null);
  const [editText, setEditText] = useState('');
  const [editSaving, setEditSaving] = useState(false);

  useEffect(() => {
    if (!userId) return navigate('/login');
    api.get(`/users/${userId}`).then(({ data }) => setUser(data)).catch(console.error);
  }, [userId, navigate]);

  useEffect(() => {
    if (!userId) return;
    setLoading(true);
    api.get('/posts', { params: { author: userId } })
      .then(({ data }) => setPosts(data))
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [userId, deleting, editSaving]);

  const handleDelete = async () => {
    setDeleting(true);
    try {
      await api.delete(`/posts/${deletePostId}`);
      setDeletePostId(null);
    } catch (err) {
      console.error(err);
    }
    setDeleting(false);
  };

  const openEdit = post => {
    setEditPostId(post._id);
    setEditText(post.text);
    setMenuPostId(null);
  };

  const handleEdit = async () => {
    setEditSaving(true);
    try {
      await api.put(`/posts/${editPostId}`, { text: editText });
      setEditPostId(null);
    } catch (err) {
      console.error(err);
    }
    setEditSaving(false);
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div className="max-w-2xl mx-auto">
      {/* табы и шапка профиля... */}
      <div className="space-y-4">
        {loading ? (
          <div>Loading posts...</div>
        ) : (
          posts.map(p => (
            <div key={p._id} className="relative group">
              <button
                className="absolute top-2 right-2 p-1 hover:bg-gray-100 rounded-full opacity-0 group-hover:opacity-100"
                onClick={() => setMenuPostId(menuPostId === p._id ? null : p._id)}
              >
                <MoreVertical className="w-5 h-5" />
              </button>
              {menuPostId === p._id && (
                <div className="absolute top-8 right-2 bg-white border rounded-lg shadow-md z-10">
                  <button onClick={() => openEdit(p)} className="flex items-center px-4 py-2 hover:bg-gray-100">
                    <Edit2 className="w-4 h-4 mr-2" /> Редактировать
                  </button>
                  <button onClick={() => setDeletePostId(p._id)} className="px-4 py-2 text-red-600 hover:bg-gray-100 w-full text-left">
                    Удалить
                  </button>
                </div>
              )}
              <Post post={p} />
            </div>
          ))
        )}
      </div>

      {/* Delete Confirmation */}
      {deletePostId && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg">
            <p className="mb-4">Удалить пост?</p>
            <button onClick={() => setDeletePostId(null)} className="mr-4">Отмена</button>
            <button onClick={handleDelete} disabled={deleting}>
              {deleting ? 'Удаляем...' : 'Да, удалить'}
            </button>
          </div>
        </div>
      )}

      {/* Edit Post Modal */}
      {editPostId && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg w-full max-w-md">
            <textarea
              className="w-full p-2 border mb-4"
              rows={4}
              value={editText}
              onChange={e => setEditText(e.target.value)}
            />
            <button onClick={() => setEditPostId(null)} className="mr-4">Отмена</button>
            <button onClick={handleEdit} disabled={editSaving}>
              {editSaving ? 'Сохраняем...' : 'Сохранить'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
