// src/pages/ProfilePage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate }            from 'react-router-dom';
import api                         from '../api';
import Post                        from '../components/Post';

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem('user') || 'null');
  const userId     = storedUser?.userId;
  const navigate   = useNavigate();

  const [user,          setUser]          = useState(null);
  const [posts,         setPosts]         = useState([]);
  const [likedPosts,    setLikedPosts]    = useState([]);
  const [favoritePosts, setFavoritePosts] = useState([]);
  const [loading,       setLoading]       = useState(true);

  const [tab,       setTab]       = useState('posts');    // 'posts' | 'likes' | 'favorites'
  const [isEditing, setIsEditing] = useState(false);
  const [fullname,  setFullname]  = useState('');
  const [username,  setUsername]  = useState('');
  const [bio,       setBio]       = useState('');
  const [location,  setLocation]  = useState('');
  const [saving,    setSaving]    = useState(false);

  // 1) Load user profile
  useEffect(() => {
    if (!userId) return navigate('/login');
    api.get(`/users/${userId}`)
      .then(({ data }) => {
        setUser(data);
        setFullname(data.fullname);
        setUsername(data.username);
        setBio(data.bio || '');
        setLocation(data.location || '');
      })
      .catch(console.error);
  }, [userId, navigate]);

  // 2) Load posts for current tab using server-side filtering
  useEffect(() => {
    if (!userId) return;
    setLoading(true);

    let params = {};
    if (tab === 'posts') {
      params = { author: userId };
    } else if (tab === 'likes') {
      params = { likedBy: userId };
    } else if (tab === 'favorites') {
      params = { favoritedBy: userId };
    }

    api.get('/posts', { params })
      .then(({ data }) => {
        if (tab === 'posts') setPosts(data);
        if (tab === 'likes') setLikedPosts(data);
        if (tab === 'favorites') setFavoritePosts(data);
      })
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [tab, userId]);

  // 3) Save profile edits
  const handleSave = async () => {
    setSaving(true);
    try {
      const { data } = await api.put(`/users/${userId}`, {
        fullname,
        username,
        bio,
        location
      });
      setUser(data);
      // Update localStorage so Navbar sees new username/fullname
      localStorage.setItem('user', JSON.stringify({
        ...storedUser,
        fullname: data.fullname,
        username: data.username
      }));
      setIsEditing(false);
    } catch (err) {
      console.error(err);
    }
    setSaving(false);
  };

  if (!user) {
    return <div className="text-center mt-12 text-gray-500">Загрузка профиля…</div>;
  }

  const initials = user.username.slice(0, 2).toUpperCase();

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow">
      {/* Profile Header */}
      <div className="flex items-center space-x-6 mb-6">
        <div className="w-20 h-20 bg-green-500 rounded-full flex items-center justify-center text-white text-3xl font-bold">
          {initials}
        </div>
        <div>
          <h1 className="text-2xl font-bold">{user.fullname}</h1>
          <p className="text-gray-500">@{user.username}</p>
        </div>
        <button
          onClick={() => setIsEditing(prev => !prev)}
          className="ml-auto px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-100"
        >
          {isEditing ? 'Отменить' : 'Редактировать профиль'}
        </button>
      </div>

      {/* Edit Form or Display */}
      {isEditing ? (
        <div className="space-y-4 mb-6">
          <div>
            <label className="block text-sm font-medium mb-1">Полное имя</label>
            <input
              value={fullname}
              onChange={e => setFullname(e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-green-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Username</label>
            <input
              value={username}
              onChange={e => setUsername(e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-green-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Bio</label>
            <textarea
              value={bio}
              onChange={e => setBio(e.target.value)}
              rows="3"
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-green-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Местоположение</label>
            <input
              value={location}
              onChange={e => setLocation(e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-green-500"
            />
          </div>
          <button
            onClick={handleSave}
            disabled={saving}
            className="px-6 py-3 bg-green-600 text-white font-semibold rounded-lg hover:bg-green-700 disabled:opacity-50"
          >
            {saving ? 'Сохраняем...' : 'Сохранить'}
          </button>
        </div>
      ) : (
        <div className="mb-6 space-y-2">
          {user.bio && <p className="text-gray-700">{user.bio}</p>}
          {user.location && (
            <p className="flex items-center text-gray-600">
              <svg className="w-5 h-5 mr-2 text-gray-500" fill="currentColor" viewBox="0 0 20 20">
                <path d="M10 2C6.686 2 4 4.686 4 8c0 4.5 6 10 6 10s6-5.5 6-10c0-3.314-2.686-6-6-6zm0 8a2 2 0 110-4 2 2 0 010 4z" />
              </svg>
              {user.location}
            </p>
          )}
        </div>
      )}

      {/* Tabs */}
      <div className="flex space-x-2 mb-6">
        {['posts', 'likes', 'favorites'].map(t => (
          <button
            key={t}
            onClick={() => setTab(t)}
            className={`flex-1 text-center py-2 rounded-lg font-semibold ${
              tab === t
                ? 'bg-green-600 text-white'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            {t === 'posts' ? 'Посты' : t === 'likes' ? 'Лайки' : 'Избранное'}
          </button>
        ))}
      </div>

      {/* Tab Content */}
      {loading ? (
        <div className="text-center text-gray-500">Загрузка…</div>
      ) : tab === 'posts' && posts.length === 0 ? (
        <div className="text-gray-500">У вас пока нет постов.</div>
      ) : tab === 'likes' && likedPosts.length === 0 ? (
        <div className="text-gray-500">Вы ещё не ставили лайки.</div>
      ) : tab === 'favorites' && favoritePosts.length === 0 ? (
        <div className="text-gray-500">У вас нет избранного.</div>
      ) : (
        (tab === 'posts' ? posts : tab === 'likes' ? likedPosts : favoritePosts).map(p => (
          <Post key={p._id} post={p} />
        ))
      )}
    </div>
  );
}
