// src/pages/UserPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api  from '../api';
import Post from '../components/Post';

export default function UserPage() {
  const { id } = useParams(); // id пользователя
  const [user,          setUser]          = useState(null);
  const [posts,         setPosts]         = useState([]);
  const [likedPosts,    setLikedPosts]    = useState([]);
  const [favoritePosts, setFavoritePosts] = useState([]);
  const [loading,       setLoading]       = useState(true);
  const [tab, setTab] = useState('posts');

  // 1) Профиль пользователя
  useEffect(() => {
    api.get(`/users/${id}`)
      .then(({ data }) => setUser(data))
      .catch(console.error);
  }, [id]);

  // 2) Данные по постам этого пользователя
  useEffect(() => {
    api.get('/posts')
      .then(({ data }) => {
        setPosts(data.filter(p => p.author._id === id));
        setLikedPosts(data.filter(p => p.likes.includes(id)));
        setFavoritePosts(data.filter(p => p.favorites.includes(id)));
      })
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [id]);

  if (loading || !user) {
    return <div className="text-center mt-12 text-gray-500">Загрузка...</div>;
  }

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow">
      <h1 className="text-3xl font-bold mb-4">{user.fullname} (@{user.username})</h1>

      {/* Табы */}
      <div className="flex space-x-2 mb-6">
        {['posts','likes','favorites'].map(t => (
          <button
            key={t}
            onClick={() => setTab(t)}
            className={`flex-1 text-center py-2 rounded-lg font-semibold
              ${tab===t 
                ? 'bg-green-600 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
          >
            {t === 'posts' ? 'Посты' : t === 'likes' ? 'Лайки' : 'Избранное'}
          </button>
        ))}
      </div>

      {/* Контент таба */}
      {tab === 'posts' && (
        posts.length === 0
          ? <div className="text-gray-500">У пользователя нет постов.</div>
          : posts.map(p => <Post key={p._id} post={p} />)
      )}

      {tab === 'likes' && (
        likedPosts.length === 0
          ? <div className="text-gray-500">Пользователь ещё не ставил лайков.</div>
          : likedPosts.map(p => <Post key={p._id} post={p} />)
      )}

      {tab === 'favorites' && (
        favoritePosts.length === 0
          ? <div className="text-gray-500">Пользователь не добавлял в избранное.</div>
          : favoritePosts.map(p => <Post key={p._id} post={p} />)
      )}
    </div>
  );
}
