// src/pages/ProfilePage.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api  from '../api';
import Post from '../components/Post';

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem('user') || 'null');
  const userId     = storedUser?.userId;
  const navigate   = useNavigate();

  const [user,     setUser]     = useState(null);
  const [tabData,  setTabData]  = useState([]);
  const [loading,  setLoading]  = useState(true);
  const [tab,      setTab]      = useState('posts'); // 'posts' | 'likes' | 'favorites'

  const [isEditing, setIsEditing] = useState(false);
  const [fullname,  setFullname]  = useState('');
  const [bio,       setBio]       = useState('');
  const [location,  setLocation]  = useState('');
  const [saving,    setSaving]    = useState(false);

  // 1. Загрузить профиль
  useEffect(() => {
    if (!userId) return navigate('/login');
    api.get(`/users/${userId}`)
      .then(({ data }) => {
        setUser(data);
        setFullname(data.fullname);
        setBio(data.bio || '');
        setLocation(data.location || '');
      })
      .catch(console.error);
  }, [userId, navigate]);

  // 2. Загрузка таба при смене tab
  useEffect(() => {
    if (!userId) return;
    setLoading(true);

    let request;
    if (tab === 'posts') {
      request = api.get('/posts', { params: { author: userId } });
    } else if (tab === 'likes') {
      request = api.get(`/users/${userId}/likes`);
    } else {
      request = api.get(`/users/${userId}/favorites`);
    }

    request
      .then(({ data }) => setTabData(data))
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [tab, userId]);

  // 3. Сохранение изменений профиля
  const handleSave = async () => {
    setSaving(true);
    try {
      const { data } = await api.put(`/users/${userId}`, { fullname, bio, location });
      setUser(data);
      localStorage.setItem('user', JSON.stringify({ ...storedUser, fullname: data.fullname }));
      setIsEditing(false);
    } catch (err) {
      console.error(err);
    }
    setSaving(false);
  };

  if (!user) {
    return <div className="text-center mt-12 text-gray-500">Загрузка профиля...</div>;
  }

  const initials = user.username.slice(0, 2).toUpperCase();

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow">
      {/* Шапка и редактирование */}
      <div className="flex items-center mb-6">
        <div className="w-20 h-20 bg-green-500 rounded-full flex items-center justify-center text-white text-3xl font-bold mr-4">
          {initials}
        </div>
        <div>
          {isEditing ? (
            <div className="space-y-2">
              <input
                value={fullname}
                onChange={e => setFullname(e.target.value)}
                className="w-full p-2 border rounded"
              />
              <textarea
                value={bio}
                onChange={e => setBio(e.target.value)}
                className="w-full p-2 border rounded"
                rows={2}
                placeholder="Bio"
              />
              <input
                value={location}
                onChange={e => setLocation(e.target.value)}
                className="w-full p-2 border rounded"
                placeholder="Location"
              />
              <button
                onClick={handleSave}
                disabled={saving}
                className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 disabled:opacity-50"
              >
                {saving ? 'Сохраняем...' : 'Сохранить'}
              </button>
              <button
                onClick={() => setIsEditing(false)}
                className="ml-2 px-4 py-2 border rounded hover:bg-gray-100"
              >
                Отменить
              </button>
            </div>
          ) : (
            <>
              <h1 className="text-2xl font-bold">{user.fullname}</h1>
              <p className="text-gray-500">@{user.username}</p>
              {user.bio && <p className="mt-2">{user.bio}</p>}
              {user.location && <p className="mt-1 text-gray-600">{user.location}</p>}
              <button
                onClick={() => setIsEditing(true)}
                className="mt-3 px-4 py-2 border rounded hover:bg-gray-100"
              >
                Редактировать профиль
              </button>
            </>
          )}
        </div>
      </div>

      {/* Табы */}
      <div className="flex mb-4 space-x-2">
        {['posts','likes','favorites'].map(t => (
          <button
            key={t}
            onClick={() => setTab(t)}
            className={`flex-1 py-2 text-center rounded ${
              tab === t
                ? 'bg-green-600 text-white'
                : 'bg-gray-200 hover:bg-gray-300'
            }`}
          >
            {t === 'posts' ? 'Посты' : t === 'likes' ? 'Лайки' : 'Избранное'}
          </button>
        ))}
      </div>

      {/* Контент таба */}
      {loading ? (
        <div className="text-center text-gray-500">Загрузка...</div>
      ) : tabData.length === 0 ? (
        <div className="text-gray-500 text-center">Пусто.</div>
      ) : (
        tabData.map(p => <Post key={p._id} post={p} />)
      )}
    </div>
  );
}
