import { Link } from 'react-router-dom';
import { Heart, Star } from 'lucide-react';

export default function Post({ post, onLike, onFavorite }) {
  const user = JSON.parse(localStorage.getItem('user') || 'null');
  const userId = user?.userId;
  const liked   = post.likes.includes(userId);
  const favored = post.favorites.includes(userId);

  return (
    <div className="bg-white p-6 rounded-lg shadow mb-6 hover:shadow-lg transition">
      <div className="flex items-center mb-3">
        <div className="w-10 h-10 bg-green-500 rounded-full flex items-center justify-center text-white font-semibold mr-3">
          {post.author.username[0].toUpperCase()}
        </div>
        <div>
          <div className="font-semibold">{post.author.username}</div>
          <div className="text-xs text-gray-500">{new Date(post.createdAt).toLocaleString()}</div>
        </div>
      </div>
      <p className="text-gray-700 whitespace-pre-wrap">{post.text}</p>
      <div className="flex items-center mt-4 space-x-4">
        <button onClick={() => onLike(post._id)} className="flex items-center">
          <Heart className={liked ? 'text-red-500' : ''} />
          <span className="ml-1">{post.likes.length}</span>
        </button>
        <button onClick={() => onFavorite(post._id)} className="flex items-center">
          <Star className={favored ? 'text-yellow-500' : ''} />
          <span className="ml-1">{post.favorites.length}</span>
        </button>
        <Link to={`/post/${post._id}`} className="ml-auto text-blue-500 hover:underline">
          Комментарии ({post.commentsCount})
        </Link>
      </div>
    </div>
  );
}
