// src/pages/UserPage.jsx
import React, { useState, useEffect } from 'react';
import { useParams }         from 'react-router-dom';
import api                   from '../api';
import Post                  from '../components/Post';

export default function UserPage() {
  const { id } = useParams();
  const [user,        setUser]        = useState(null);
  const [tabData,     setTabData]     = useState([]);
  const [loading,     setLoading]     = useState(true);
  const [tab,         setTab]         = useState('posts'); // 'posts' | 'likes' | 'favorites'

  // Загрузка профиля
  useEffect(() => {
    api.get(`/users/${id}`)
      .then(({ data }) => setUser(data))
      .catch(console.error);
  }, [id]);

  // Загрузка таба
  useEffect(() => {
    setLoading(true);
    let request;
    if (tab === 'posts') {
      request = api.get('/posts', { params: { author: id } });
    } else if (tab === 'likes') {
      request = api.get(`/users/${id}/likes`);
    } else {
      request = api.get(`/users/${id}/favorites`);
    }
    request
      .then(({ data }) => setTabData(data))
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [tab, id]);

  if (!user) {
    return <div className="text-center mt-12 text-gray-500">Загрузка пользователя...</div>;
  }

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow">
      <h1 className="text-2xl font-bold mb-2">{user.fullname}</h1>
      <p className="text-gray-500 mb-4">@{user.username}</p>

      {/* Табы */}
      <div className="flex mb-4 space-x-2">
        {['posts','likes','favorites'].map(t => (
          <button
            key={t}
            onClick={() => setTab(t)}
            className={`flex-1 py-2 text-center rounded-lg ${
              tab === t
                ? 'bg-green-600 text-white'
                : 'bg-gray-200 hover:bg-gray-300'
            }`}
          >
            {t === 'posts' ? 'Посты' : t === 'likes' ? 'Лайки' : 'Избранное'}
          </button>
        ))}
      </div>

      {/* Контент таба */}
      {loading ? (
        <div className="text-center text-gray-500">Загрузка...</div>
      ) : tabData.length === 0 ? (
        <div className="text-gray-500 text-center">Пусто.</div>
      ) : (
        tabData.map(p => <Post key={p._id} post={p} />)
      )}
    </div>
  );
}
