import { useState, useEffect } from 'react';
import api from '../api';

export default function SearchPage() {
  const [query, setQuery] = useState('');
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);

  // Функция запроса
  const fetchUsers = async q => {
    setLoading(true);
    try {
      const res = await api.get('/users', { params: { search: q } });
      setUsers(res.data);
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  // При монтировании и при изменении query
  useEffect(() => {
    const delay = setTimeout(() => {
      fetchUsers(query);
    }, 300); // небольшая задержка для избежания слишком частых запросов
    return () => clearTimeout(delay);
  }, [query]);

  return (
    <div className="max-w-md mx-auto">
      <h2 className="text-3xl font-bold mb-4">Поиск пользователей</h2>
      <input
        type="text"
        placeholder="Поиск по имени..."
        value={query}
        onChange={e => setQuery(e.target.value)}
        className="w-full p-3 mb-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
      />

      {loading ? (
        <div className="text-center text-gray-500">Загрузка...</div>
      ) : (
        <ul>
          {users.map(u => (
            <li key={u._id} className="p-3 bg-white rounded-lg shadow mb-3">
              <div className="font-semibold">{u.username}</div>
              <div className="text-gray-600 text-sm">{u.fullname}</div>
            </li>
          ))}
          {users.length === 0 && (
            <li className="text-gray-500">Ничего не найдено.</li>
          )}
        </ul>
      )}
    </div>
  );
}
