// src/pages/RegisterPage.jsx
import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../api';

export default function RegisterPage() {
  const [username, setUsername] = useState('');
  const [fullname, setFullname] = useState('');
  const [email, setEmail]       = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading]   = useState(false);
  const [error, setError]       = useState('');
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      // 1) Регистрация
      const regRes = await api.post('/users', {
        username,
        fullname,
        email,
        password
      });

      // 2) Сразу логиним по email
      const loginRes = await api.post('/users/login', {
        email,
        password
      });
      const { userId, username: uName, fullname: fName } = loginRes.data;

      // 3) Сохраняем и перенаправляем
      localStorage.setItem(
        'user',
        JSON.stringify({ userId, username: uName, fullname: fName })
      );
      navigate('/feed');
    } catch (err) {
      // если пользователь уже создан или email неверен на логине
      setError(
        err.response?.data?.error === 'User not found'
          ? 'Неверный email или пароль'
          : err.response?.data?.error || 'Ошибка регистрации'
      );
    }
    setLoading(false);
  };

  return (
    <div className="max-w-md mx-auto mt-12 bg-white p-6 rounded-lg shadow">
      <h2 className="text-2xl font-bold mb-4 text-center">Регистрация</h2>
      {error && <div className="text-red-500 mb-4">{error}</div>}
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Имя пользователя"
          value={username}
          onChange={e => setUsername(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg"
          required
        />
        <input
          type="text"
          placeholder="Полное имя"
          value={fullname}
          onChange={e => setFullname(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg"
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg"
          required
        />
        <input
          type="password"
          placeholder="Пароль"
          value={password}
          onChange={e => setPassword(e.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg"
          required
        />
        <button
          type="submit"
          disabled={loading}
          className="w-full py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50"
        >
          {loading ? 'Обработка...' : 'Зарегистрироваться'}
        </button>
      </form>
      <p className="text-sm text-center mt-4">
        Уже есть аккаунт?{' '}
        <Link to="/login" className="text-green-600 hover:underline">
          Войти
        </Link>
      </p>
    </div>
  );
}
